mapper.select:


    p.del_flag = '0'
     and p.category = #{category}
     and p.title like concat('%', #{title}, '%')
     and p.status = #{status}
     and p.price >= #{params.minPrice}
     and p.price <= #{params.maxPrice}

order by p.create_time desc

mapper.update:
update campus_product
user_id = #{userId}, title = #{title}, description = #{description}, category = #{category}, image_urls = #{imageUrls}, price = #{price}, contact_info = #{contactInfo}, view_count = #{viewCount}, status = #{status}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where product_id = #{productId}

mapper.select:
select count(*)
from campus_product_favorite
where product_id = #{productId} and user_id = #{userId}



    p.del_flag = '0' and p.user_id = #{userId}
     and p.category = #{category}
     and p.status = #{status}

order by p.create_time desc
