mapper.select:
select t.topic_id, t.user_id, t.content, t.image_urls, t.status, t.del_flag, t.create_by, t.create_time, t.update_by, t.update_time, t.remark, t.topic_type, u.nick_name, CASE WHEN u.avatar IS NOT NULL AND u.avatar != '' THEN CONCAT(u.avatar, '?v=', u.update_time) ELSE u.avatar END as avatar, (select count(*) from campus_topic_like l where l.topic_id = t.topic_id) as like_count, (select count(*) from campus_topic_like l where l.topic_id = t.topic_id and l.user_id = #{userId}) as liked from campus_topic t left join sys_user u on u.user_id = t.user_id
AND (t.content like concat('%', #{content}, '%') OR u.nick_name like concat('%', #{content}, '%'))
and t.topic_type = #{topicType}

mapper.update:
update campus_topic
user_id = #{userId}, content = #{content}, image_urls = #{imageUrls}, topic_type = #{topicType}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where topic_id = #{topicId}
